#include "fs/operations.h"
#include <assert.h>
#include <string.h>

int main() {

    char *str = "";
    char *path = "/f1";
    char buffer[4077];

    assert(tfs_init() != -1);

    int f;
    ssize_t r;

    f = tfs_open(path, TFS_O_CREAT);
    assert(f != -1);

    r = tfs_write(f, str, strlen(str));
    assert(r == strlen(str));

    assert(tfs_close(f) != -1);

    f = tfs_open(path, 0);
    assert(f != -1);

    r = tfs_read(f, buffer, sizeof(buffer) - 1);
    assert(r == strlen(str));
    printf("buffer: %s\n string: %s\n", buffer, str);
    
    printf("r: %ld\n", r);
    buffer[r] = '\0';
    assert(strcmp(buffer, str) == 0);

    assert(tfs_close(f) != -1);
	
	tfs_copy_to_external_fs(path,"test1.txt");

    printf("Successful test.\n");

    return 0;
}
